mapper.select:
select comment_id, content, userid, write_date
from comment
where target_post_id = ${param1}
order by target_parent asc, depth asc, write_date asc;

mapper.update:
update comment
set content=#{content}
where comment_id=${comment_id};

update comment
set content='삭제되었습니다.'
where comment_id=${param1};
