mapper.select:
select post_id, title, userid, content, views, date_format(write_date,'%m-%d %h:%i') as write_date,bodypart
from board
where post_id = #{param1}

mapper.update:
update board
set views = views + 1
where post_id = #{param1}

update board
set title = #{title}, content = #{content}
where post_id = #{post_id}
